function pageprocessorcannoneerAthleticsDetails(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  $scope.type = $rootScope.athleticFeature;


  //$rootScope.appDisplayName = $rootScope.selSports;

  if ($scope.type == "news") {
    $scope.feed = $rootScope.newsfeeds[$rootScope.athleticIndex];
    console.log($scope.feed);
    var template = pagedef.datatemplate;
    var data1 = $compile($(template))($scope);
    $("#appContent").append(data1);

  } else if ($scope.type == "sch") {
    $scope.feed = $rootScope.schfeeds[$rootScope.athleticIndex];
    var template = pagedef.datatemplate;
    var data1 = $compile($(template))($scope);
    $("#appContent").append(data1);
  } else if ($scope.type == "sco") {
    $scope.feed = $rootScope.scofeeds[$rootScope.athleticIndex];
    var template = pagedef.datatemplate;
    var data1 = $compile($(template))($scope);
    $("#appContent").append(data1);

  }
  $scope.openurl = function() {
    if (window.device) {
      window.open($scope.feed.link, "_blank", "location=no");
    } else {
      window.open($scope.feed.link);
    }

  };

  $scope.openFeed = function(index) {
    if (window.device) {
      window.history.back();
    } else {
      $location.path('/app/cannoneerAthletics/cannoneerAthletics')
    }
  };

