    return $sce.trustAsHtml(data);
  };
  $scope.convertToDate = function(stringDate) {

    var dateOut = new Date(stringDate);

    dateOut.setDate(dateOut.getDate());

    var hr = dateOut.getUTCHours();
    var mint = dateOut.getUTCMinutes();
    if (hr == 0 && mint == 0) {

      var dateString = dateOut.toString().substring(0, 16);

      var dateTimeString = dateString + "  TBA";
      return dateTimeString;

    } else {
      return dateOut;
    }
  };
  $scope.showButtonNext = true;
  $scope.showButtonPrev = true;
  $scope.isDisabled = false;
  $scope.index = $rootScope.athleticIndex;

  $scope.next = function() {
    var len = $rootScope.newsfeeds.length;
    try {
      console.log($scope.index);
      console.log($rootScope.newsfeeds);
      console.log($rootScope.newsfeeds[$scope.index]);

      if ($scope.index < len - 1) {
        $scope.feed = $rootScope.newsfeeds[++$scope.index];
