    try {
      console.log($scope.index);
      if ($scope.index < len - 1) {
        $scope.feed = $rootScope.scofeeds[++$scope.index];
        $scope.showButtonPrev = true;
      } else {
        return 0;
        if (window.device) {
          navigator.notification.alert(
            'No More News Feeds',
            null,
            '',
            'OK'
          );
        } else {
          alert('No More News Feeds')
        }
      }
    } catch (e) {
      console.log(e);
    }
    $window.scrollTo(0, 0);
  };
  $scope.prevSco = function() {
    try {
      console.log($scope.index);
      if ($scope.index > 0) {
        $scope.feed = $rootScope.scofeeds[--$scope.index];
        $scope.showButtonNext = true;
      } else {
        return 0;
        if (window.device) {
          navigator.notification.alert(
