        }

      }
    } catch (e) {
      console.log(e);
    }
    $window.scrollTo(0, 0);
  };
  $scope.convertToDateNews = function(stringDate) {
    var dateOut = new Date(stringDate);
    dateOut.setDate(dateOut.getDate());
    return dateOut;
  }

  $scope.locationFilter = function(title) {
    var filText = "Location: " + title.substr(0, title.indexOf('vs.'));
