    "appKey": pagedef.appKey,
    "tenant": $rootScope.tenant,
    "licenseKey": pagedef.licenseKey
  }

  $rootScope.dummyValenceGetAPI = function(url, callBack) {
    $.blockUI(),
      $http({
        method: "GET",
        url: url,
        data: {}
      }).then(function(res) {
        $.unblockUI(), callBack(res.data, res.status, res.headers, res.config);
      }, function(err) {
        return $.unblockUI(), callBack(err.data, err.status, err.headers, err.config);
      });
  }


  $rootScope.ssoOpened = false;
  var iabRef;

  $scope.firstTime = true;
  $scope.goBack = function() {
    console.log("in goBack")
    $scope.page = 1;
  }

  function getCourseList() {
    $rootScope.dummyValenceGetAPI(courseListUrl, function(data) {
      $.unblockUI();
      var courseslist = [];
      angular.forEach(data.Items, function(val, i) {
        if ((val.OrgUnit.Type.Code == "Course Offering") && (val.Access.IsActive) && (val.Access.CanAccess)) {
          courseslist.push(val);
        }
      });
      $scope.courseslist = courseslist;
      $rootScope.courseslist = courseslist;
      $scope.$apply();
    });
  }

  $scope.page = 1;

  $rootScope.courseslist = "";
  getCourseList();


  $scope.trusthtml = function(data) {
    return $sce.trustAsHtml(data);
  }

  $scope.totalTopics = 0
  $scope.topicsCompleted = 0;

  var topicsCompleted = 0;
  var totalTopics = 0;
  var countObj = {
    "totalTopics": 0,
    "topicsCompleted": 0,
    "currentModuleTopics": 0
  }

  function formatCourseContent(modules, countObj) {
    angular.forEach(modules, function(value, key) {
      countObj.currentModuleTopics = 0;
      // first check the topics for the module
      angular.forEach(value.Topics, function(topic, topicIndex) {
        if (!topic.Unread) {
          countObj.topicsCompleted++;
          countObj.currentModuleTopics++;
        }
      });
      countObj.totalTopics += value.Topics.length;
      countObj.currentModuleTopics += value.Topics.length;
      //check the inner modules
      if (value.Modules.length > 0) {
        formatCourseContent(value.Modules, countObj);
      }
      value.topicsCount = countObj.currentModuleTopics;
    });
    return countObj;
  }

  function calculateTopicsCount(module) {
    var count = 0;
    console.log("Module " + module.Title + " count =" + count + " : topics length= " + module.Topics.length);
    count += (module && module.Topics) ? module.Topics.length : 0;
    angular.forEach(module.Modules, function(val1, key1) {
      count += val1.Topics.length;
      console.log(val1.Title + " module length = " + val1.Modules.length + "topicCount= " + count);
      if (val1.Modules && val1.Modules.length > 0) {
        count += calculateTopicsCount(val1);
      }
    });
    return count;
  }

  function getModuleDetails(module, callBack) {
    module.groupTopicCount = calculateTopicsCount(module);
    $rootScope.ValenceGetAPI(config, "/d2l/api/le/1.12/" + $scope.course.OrgUnit.Id + "/content/modules/" + module.ModuleId, function(moduleInfo) {
      if (moduleInfo && moduleInfo.ModuleStartDate) {
        module.ModuleStartDate = moduleInfo.ModuleStartDate;
      }
      if (moduleInfo && moduleInfo.Description && moduleInfo.Description.Html) {
        module.moduleInfo = moduleInfo.Description.Html;
      }
      console.log(module)
      return callBack();
    });
  }

  $scope.loadModuleDetails = function(module) {
    if (module.executed) {
      return;
    } else {
      console.log("calling module details:")
      $rootScope.ValenceGetAPI(config, "/d2l/api/le/1.12/" + $scope.course.OrgUnit.Id + "/content/modules/" + module.ModuleId, function(moduleInfo) {
        module.executed = true;
        if (moduleInfo && moduleInfo.ModuleStartDate) {
          module.ModuleStartDate = moduleInfo.ModuleStartDate;
        }
        angular.forEach(module.Modules, function(val, key) {
          getModuleDetails(val, function() {

          });
        });
      });
    }
  }

  $scope.loadSUBModuleDetails = function(module) {
    console.log(module);
    if (module.executed) {
      return;
    } else {
      console.log("calling module details:")
      $rootScope.ValenceGetAPI(config, "/d2l/api/le/1.12/" + $scope.course.OrgUnit.Id + "/content/modules/" + module.ModuleId, function(moduleInfo) {
        module.executed = true;
        if (moduleInfo && moduleInfo.ModuleStartDate) {
          module.ModuleStartDate = moduleInfo.ModuleStartDate;
        }
        if (moduleInfo && moduleInfo.Description && moduleInfo.Description.Html) {
          module.moduleInfo = moduleInfo.Description.Html;
        }
      });
    }
  }


  $scope.showSubModuleDetails = function(subModule) {
    console.log(subModule);
    $scope.subContent = subModule;
    $scope.page = 3;
  }

  function getTopLevelModuleDetails(module) {
    var moduleInfo;
    angular.forEach($scope.modules, function(value, key) {
      if (value.Id == module.ModuleId) {
        moduleInfo = value;
        if (moduleInfo && moduleInfo.ModuleStartDate) {
          module.ModuleStartDate = moduleInfo.ModuleStartDate;
        }
        if (moduleInfo && moduleInfo.Description && moduleInfo.Description.Html) {
          module.moduleInfo = moduleInfo.Description.Html;
        }
        console.log(module)
      }
    });
    return;
  }

  $scope.showCourseDetails = function(course) {
    $scope.page = 2;
    $scope.course = course;
    $scope.selTab = 1;
    $rootScope.dummyValenceGetAPI(courseNewsUrl, function(res) {
      $scope.coursenews = res;
    });
    $rootScope.dummyValenceGetAPI(courseTocUrl, function(res) {
      $rootScope.dummyValenceGetAPI(courseModuleBulkUrl, function(modules) {
        $scope.modules = modules;
        $scope.content = res;
        $scope.totalTopics = 0
        $scope.topicsCompleted = 0;
        var topicsCompleted = 0;
        var totalTopics = 0;
        var countObj = {
          "totalTopics": 0,
          "topicsCompleted": 0,
          "currentModuleTopics": 0
        }
        formatCourseContent($scope.content.Modules, countObj);
        $scope.totalTopics = countObj.totalTopics;
        $scope.topicsCompleted = countObj.topicsCompleted;
        $scope.completedPercentage = "0"
        if (countObj.totalTopics > 0) {
          $scope.completedPercentage = ((countObj.topicsCompleted / countObj.totalTopics) * 100).toFixed(2);
        }
        angular.forEach($scope.content.Modules, function(value, key) {
          getTopLevelModuleDetails(value);
        });
      });
    });
    var lastId = 0;
    $scope.grades = [];
    $scope.gradevals = [];
    $rootScope.dummyValenceGetAPI(gradeCategoriesUrl, function(categories) {
      $scope.grades = categories;
      $rootScope.dummyValenceGetAPI(gradesUrl, function(grades) {
        $scope.gradevals = grades;
        angular.forEach($scope.gradevals, function(val3, k) {
          if (val3.CategoryId > 0) {
            lastId = val3.CategoryId;
          } else {
            angular.forEach($scope.grades, function(valx, x) {
              if (valx.Id == lastId) {
                valx.Grades.push(val3);
              }
            });
          }
        });
        if ($scope.grades.length == 0 && $scope.gradevals.length > 0) {
          angular.forEach($scope.gradevals, function(valx, x) {
            var catObj = {
              "Id": 0,
              "Grades": [],
              "Name": valx.Name
            };
            catObj.Grades.push(valx);
            $scope.grades.push(catObj);
          });
        }
        angular.forEach($scope.grades, function(val, i) {
          angular.forEach(val.Grades, function(val2, j) {
            val2.gradevalue = null;
          });
        });
      });
    });
    $scope.showNews();
  }

