  $scope.uploadProfileImage = function() {
    if (window.device) {
      $scope.openCamera();
    } else {
      try {
        console.log('testing else');
        $("#uploadProfileImg").change(function() {
          if (this.files && this.files[0]) {

            if (this.files[0].name.split('.').pop().toLowerCase() == 'png' || this.files[0].name.split('.').pop().toLowerCase() == 'jpg' || this.files[0].name.split('.').pop().toLowerCase() == 'jpeg') {

              var FR = new FileReader();

              FR.onload = function(e) {

                $scope.targetImage = e.target.result;

                //Set image width before cropping (modal-image)
                $scope.getmodalCropUrl = $scope.targetImage;
                console.log('modal image before crop ', $scope.getmodalCropUrl);
                var winWidth = $(window).width();

                if (winWidth <= 549) {
                  $("#profileCropModal .cropModalContent").width(winWidth - 13);
                }

                var Readerimg = new Image();
                Readerimg.src = e.target.result;

                Readerimg.onload = function() {
                  $scope.ReaderimgWid = this.width;
                  $scope.ReaderimgHeight = this.height;

                  if ($scope.ReaderimgWid <= 200 && $scope.ReaderimgHeight <= 200) {
                    alert('Please Use Bigger Image.');
                    return false;
                  }

                  if ($scope.ReaderimgWid < 550) {

                    $scope.ModalCropImage = "";

                    $("#profileCropModal").modal("show");
                    $scope.ModalCropImage = e.target.result;

                    (function bindDrag() {
                      var container = $(".ModalcropBody");
                      $("#canvas").resizable({
                        aspectRatio: true,
                        handles: 'n,s,e,w,ne,se,nw,sw',
                        minWidth: 115,
                        minHeight: 115,
                        maxWidth: $scope.pixelsWid,
                        maxHeight: $scope.pixelsHeight,
                        containment: container
                      }).draggable({
                        containment: container,
                        scroll: false
                      });

                    })();


                    $('#canvas').css('width', 120).css('height', 120);
                    $scope.$digest();
                  } else {
                    $scope.addCropModal();
                  }

                }
              };

              if (this.files[0].size > 2000000) {

                if (window.device) {
                  navigator.notification.alert("Image size is too large. Please Upload below 2MB");
                } else {
                  alert("Image size is too large. Please Upload below 2MB");
                }
                return;
              }

              FR.readAsDataURL(this.files[0]);
              $("#uploadProfileImg")[0].value = '';
            } else {
              if (window.device) {
                navigator.notification.alert("Only png, jpg file types are allowed.", null, '', 'Ok');
              } else {
                alert("Only png, jpg file types are allowed.");
              }
            }
          }
        });

        document.getElementById("uploadProfileImg").click();
      } catch (e) {
        alert(e);
      }

    }

  };




  $scope.getBase64 = "";
  $scope.imageObj = new Image();

  //Resize and Crop Starts(Robin) 


  setTimeout(function() {
    $scope.$digest();
  }, 300)

  $scope.bindDrag = function() {
    var container = $(".ModalcropBody");
    $("#canvas").resizable({
      aspectRatio: true,
      handles: 'n,s,e,w,ne,se,nw,sw',
      minWidth: 115,
      minHeight: 115,
      maxWidth: $scope.pixelsWid,
      maxHeight: $scope.pixelsHeight,
      containment: container
    }).draggable({
      containment: container,
      scroll: false
    });

  }

  var convertCanvas = document.getElementById("modalCanvas");
  var ctx = convertCanvas.getContext("2d");

  $scope.addCropModal = function() {
    var getModalWidth = $("#profileCropModal .cropModalContent").width();
    $("#profileCropModal").modal("show");
    $(".modal-backdrop").css("display", "none");
    $scope.decreaseValue = $scope.ReaderimgWid - getModalWidth;
    $scope.getPerDecresceVal = ($scope.decreaseValue / $scope.ReaderimgWid) * 100;
    $scope.pixelsWid = $scope.ReaderimgWid - $scope.ReaderimgWid * ($scope.getPerDecresceVal / 100);
    $scope.pixelsHeight = $scope.ReaderimgHeight - $scope.ReaderimgHeight * ($scope.getPerDecresceVal / 100);
    var convertdCanvasWidth = $scope.pixelsWid;
    var convertdCanvasHeight = $scope.pixelsHeight;
    var image = new Image();
    image.src = $scope.targetImage;
    image.onload = function() {
      image.src = $scope.targetImage;
      width = image.width;
      height = image.height;
      var convertCanvas = document.getElementById("modalCanvas");
      var ctx = convertCanvas.getContext("2d");


      if ($(window).width() <= 768) {
        var getModalWidth = $(window).width();
        var popupWidth = getModalWidth - 15;
      } else {
        var getModalWidth = $("#profileCropModal .cropModalContent").width();
        var popupWidth = getModalWidth - 32;
      }
      if (width > height) {
        if (width > popupWidth) {
          height *= popupWidth / width;
          width = popupWidth;
        }
      } else {
        if (height > popupWidth) {
          width *= popupWidth / height;
          height = popupWidth;
        }
      }
      convertCanvas.width = width;
      convertCanvas.height = height;
      $scope.convertdCanvasWidth = width;
      $scope.convertdCanvasHeight = height;
      if (window.device) {
        $('.cropModalContent').width(width);
      }
      ctx.drawImage(image, 0, 0, $scope.convertdCanvasWidth, $scope.convertdCanvasHeight);
      fitToContainer();
      image.src = $scope.targetImage;
      $scope.ModalCropImage = convertCanvas.toDataURL();
      $scope.$apply();
    }

    function fitToContainer() {
      $scope.bindDrag();
    }
  }


  $('#profileCropModal').modal({
    backdrop: 'static',
    keyboard: false,
    show: false
  });
  //Resize and Crop End

  $scope.uploadProfileCover = function() {
    if (window.device) {
      $scope.openCamera();
    } else {
      //console.log('filechooser');
      try {
        $("#uploadProfileCvr").change(function() {
          if (this.files && this.files[0]) {
            if (this.files[0].name.split('.').pop().toLowerCase() == 'png' || this.files[0].name.split('.').pop().toLowerCase() == 'jpg' || this.files[0].name.split('.').pop().toLowerCase() == 'jpeg') {
              var FR = new FileReader();
              FR.onload = function(e) {

                $scope.newProfileBackground = e.target.result;
                //$('#profileImg').attr("src", $scope.newProfileImage);

                $scope.currentUser.profileBackground = e.target.result;
                $scope.$apply();
              };

              if (this.files[0].size > 5000000) {
                if (window.device) {
                  navigator.notification.alert("Image size is too large.  Please upload below 5MB");
                } else {
                  alert("Image size is too large.  Please upload above 500KB");
                }
                return;
              }
              if (this.files[0].size < 100000) {

                if (window.device) {
                  navigator.notification.alert("Image size is too small. Please upload above 100KB");
                } else {
                  alert("Image size is too small. Please upload above 100KB");
                }
                return;
              }
              FR.readAsDataURL(this.files[0]);
              $("#uploadProfileCvr")[0].value = '';
            } else {
              if (window.device) {
                navigator.notification.alert("Only png, jpg file types are allowed.", null, '', 'Ok');
              } else {
                alert("Only png, jpg file types are allowed.");
              }
            }
          }
        });
        document.getElementById("uploadProfileCvr").click();
      } catch (e) {
        alert(e);
      }
    }
  };

  function updateUserProfile(callBack) {
    url = '/unifydidentity/user';
    $rootScope.callAPI(url, 'POST', $scope.currentUser, function(res) {
      $.unblockUI();
      if (!res.data) {
        if (window.device) {
          navigator.notification.alert(
            'Unable to register user.', // message
            null, // callback
            '', // title
            'OK' // buttonName
          );
        } else {
          alert('Unable to register user.');
        }
        return callBack("Error");
      }
      var data = res.data;
      $rootScope.user['id'] = data['id'];
      $rootScope.user['profileBackground'] = data['profileBackground'];
      $rootScope.user['firstName'] = data['firstName'];
      $rootScope.user['lastName'] = data['lastName'];
      $rootScope.user['profileImage'] = data['profileImage'];
      $rootScope.user['role'] = data['role'];
      $rootScope.user['tenant'] = data['tenant'];
      $rootScope.user['email'] = data['email'];
      $rootScope.user['phone'] = data['phone'];
      $rootScope.user['areaOfInterest'] = data['areaOfInterest'];
      $rootScope.user['channels'] = data['channels'];
      return callBack();
    });
  }



  $scope.preselected = function(index) {
    if (!$scope.areaOfInterest[index].enable)
      return;

    $scope.isDirty = true;
    $scope.areaOfInterest[index].selected = !$scope.areaOfInterest[index].selected;
    if ($scope.areaOfInterest[index].selected)
      $scope.selectedCount++;
    else
      $scope.selectedCount--;

  };

  $scope.cancelEdit = function() {
    //$scope.currentUser.profileImage = "";
    $scope.currentUser['profileBackground'] = $scope.currentUserTemp['profileBackground'];
    $scope.currentUser['profileImage'] = $scope.currentUserTemp['profileImage'];
    $scope.currentUser['areaOfInterest'] = $scope.currentUserTemp['areaOfInterest'];
    $scope.currentUser['profileImageCover'] = $scope.currentUserTemp['profileImageCover'];

    //console.log(JSON.stringify($scope.currentUserTemp));
    //console.log(JSON.stringify($scope.currentUser));
    $scope.editMode = false;
    $scope.isDirty = false;
    $scope.newProfileImage = "";
    for (var i = 0; i < $scope.areaOfInterest.length; i++) {
      $scope.areaOfInterest[i].selected = false;
      angular.forEach($scope.currentUser.areaOfInterest, function(value, key) {
        if (value == $scope.areaOfInterest[i].id) {
          $scope.areaOfInterest[i].selected = true;
        }
      });
    }
    console.log("55555---")

    $scope.$apply();
  }




  var windowWidth = $(window).width();
  setTimeout(function() {
    $('.u8PopupBg ').on('show.bs.modal', function(e) {
      setTimeout(function() {
        $(".modal-backdrop.in").hide();
      }, 500);
    });
  }, 500);
  //fix modal popup issue
  setTimeout(function() {
    if (windowWidth < 768) {
      $('.u8PopupBg ').on('show.bs.modal', function(e) {
        $('.owl-stage-outer').addClass('remove-carousel');
        $('.owl-stage').addClass('remove-carousel');
      });

      $('.u8PopupBg ').on('hidden.bs.modal', function(e) {
        $('.owl-stage-outer').removeClass('remove-carousel');
        $('.owl-stage').removeClass('remove-carousel');
      });
    }
  }, 1000);

}

