function authFunction(uname, password, $http, $rootScope, success, error) {
  /* callAPI, refreshToken and callOpenAPI are moved to services in framework */

  var url = $rootScope.GatewayUrl + "/unifydidentity/open/oauth2/token";

  /*var option = {
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
        'X-TENANT-ID': $rootScope.user.tenant
      },
      url: url,
      method: 'POST',
      form: {
        username: uname,
        password: password
      },
      json: true
    };
    
    var req = {
      url: 'https://kryptosda.kryptosmobile.com/kryptosds/utils/proxy',
      data: option,
      method: 'POST'
    };
    console.log(JSON.stringify(req));
    $http(req).then(function successCallback(res) {
      console.log('success');
      console.log(JSON.stringify(res));
      success(res.data);
    }, function errorCallback(err) {
      console.log('error');
      console.log(JSON.stringify(err));
      error(err)
    });*/


  $.ajax({
    type: 'POST',
    url: url,
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded',
      'X-TENANT-ID': $rootScope.user.tenant
    },
    data: {
      username: uname,
      password: password
    },
    dataType: 'json',
    encode: true
  }).done(function(res) {
    success(res);

    $rootScope.getNotificationBadgeMobile();
    var badgeInterval = 2 * 60 * 1000; //every two minute
    setInterval($rootScope.getNotificationBadgeMobile, badgeInterval);
  }).fail(function(err) {
    $.unblockUI();

    if (window.device) {
      navigator.notification.alert("Unable to login, please check your credentials", null, '', 'Ok');
    } else {
      alert("Unable to login, please check your credentials");
    }
  });

  /********************************************************************************************/
  //  Added callGlobalAPI as a wrapper to call authenticated microservice APIs from tenant CEAI
  /********************************************************************************************/
  $rootScope.callGlobalAPI = function(url, method, data, callback) {
      try {
        if (!$rootScope.isblocking) $.blockUI();
        var apiEndPoint = $rootScope.GatewayUrl + url;
        var req = {
          method: method,
          url: apiEndPoint,
          headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json',
            'Authorization': 'Bearer ' + $rootScope.user.accessToken,
            'X-TENANT-ID': 'CEAI',
            'X-TENANT-DOMAIN': $rootScope.user.tenantdomain
          },
          data: data
        }
        $http(req).then(function successCallback(res) {
          if (!$rootScope.isblocking) {
            $.unblockUI();
          }
          return callback(res);
        }, function errorCallback(err) {
          if (!$rootScope.isblocking) {
            $.unblockUI();
          }
          return callback();
        });
      } catch (e) {
        if (!$rootScope.isblocking) {
          $.unblockUI();
        }
        return callback();
      }
    }
    /***************************************************************************/
    //  Added callAPI as a wrapper to call open microservice APIs
    /***************************************************************************/
  $rootScope.callOpenAPI = function(option, cb) {
    var req;
    if (window.device) {
      req = option
    } else {
      req = {
        url: 'https://kryptosda.kryptosmobile.com/kryptosds/utils/proxy',
        data: option,
        method: 'POST'
      }
    }

    if (!$rootScope.isblocking) $.blockUI();

    $http(req).then(function successCallback(res) {
      if (!$rootScope.isblocking) {
        $.unblockUI();
      }
      cb(null, res.data);
    }, function errorCallback(err) {
      if (!$rootScope.isblocking) {
        $.unblockUI();
      }
      cb(err, null);
    });

  };

  /***************************************************************************/
  //  Added callAPI as a wrapper to call authenticated microservice APIs
  /***************************************************************************/
  $rootScope.callAPI = function(url, method, data, callback) {
    try {
      if (!$rootScope.user.accessToken && url.indexOf("/open/") == -1) {
        console.log("User doesn't a token, this should be a public User.");
        $.unblockUI();
        return callback();
      }
      if (!$rootScope.isblocking) $.blockUI();
      var apiEndPoint = $rootScope.GatewayUrl + url;
      /*if(url.indexOf("/unifyedrbac") == 0) {
       apiEndPoint = "http://localhost:8085" + url.substring(12)
       }*/

      var req = {
        "method": method,
        "url": apiEndPoint,
        "headers": {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Authorization': 'Bearer ' + $rootScope.user.accessToken,
          'X-TENANT-ID': $rootScope.user.tenant,
          'X-TENANT-DOMAIN': $rootScope.user.tenantdomain,
          'principal-user': $rootScope.username
        },
        data: data,
        json: true
      };

      if ($rootScope.site && $rootScope.site._id) {
        req.headers['site-id'] = $rootScope.site._id;
      }

      /*if(url.indexOf("/unifyedrbac") == 0) {
       req.url = "http://localhost:8084" + url.substring(12);
       req.headers['site-id'] = $rootScope.site._id;
       }*/


      $http(req).then(function successCallback(res) {
        if (!$rootScope.isblocking) {
          $.unblockUI();
        }
        return callback(res);
      }, function errorCallback(err) {
        if (!$rootScope.isblocking) {
          $.unblockUI();
        }
        //return callback();
        /* Commented the code below since token refersh is not working */
        if (err.status == 404) {
          return callback();
        } else if (apiEndPoint.indexOf("/open/") != -1) {
          return callback();
        } else if (err.status == 0 || err.status == 401) {
          console.log("in callAPI, URL : " + apiEndPoint + " : error status=" + err.status);
          $rootScope.refreshToken(url, method, data, callback);
          return callback();
        }
      });
    } catch (e) {
      console.log(e);
      if (!$rootScope.isblocking) {
        $.unblockUI();
      }
      return callback();
    }
  }

}

