  $scope.updateNews = function(index, news) {
    var item = "news" + index;
    var eL = angular.element(document.getElementById(item));
    if (eL.children().length == 0) {
      eL.append(news.Body.Html);
      angular.forEach(eL.find('a'), function(value, iterator, obj) {
        var aEl = angular.element(value);
        var tempHref = aEl.attr("href");
        if ($rootScope.webApp) {
          if (tempHref && tempHref.lastIndexOf("/", 0) == 0) {
            aEl.attr("href", homepage + tempHref);
          }
        } else {
          aEl.bind('click', function(e) {
            //console.log("element");
            //console.log(e);
            var vhref = e.srcElement.href;
            if (vhref.indexOf("file://") == -1) {
              //Nothing
              e.preventDefault();
              e.stopPropagation();
              var iabExtLink = window.open(vhref, "_blank", "location=no,hidden=no,clearcache=no,enableViewportScale=yes");
            } else {
              e.preventDefault();
              e.stopPropagation();
              //create a D2L session and show the URL
              var linkUrl = homepage + vhref.replace("file://", "");
              $scope.openUrlInBrowser(linkUrl);
              return;
            }
          });
        }
      });
      angular.forEach(eL.find('img'), function(value, iterator, obj) {
        var imgEl = angular.element(value);
        var tempHref = imgEl.attr("src");
        imgEl.attr("src", homepage + tempHref);
      });
    }
    return "";
  }

}

