  $scope.addCropModal = function() {
    var getModalWidth = $("#profileCropModal .cropModalContent").width();
    $("#profileCropModal").modal("show");
    $(".modal-backdrop").css("display", "none");
    $scope.decreaseValue = $scope.ReaderimgWid - getModalWidth;
    $scope.getPerDecresceVal = ($scope.decreaseValue / $scope.ReaderimgWid) * 100;
    $scope.pixelsWid = $scope.ReaderimgWid - $scope.ReaderimgWid * ($scope.getPerDecresceVal / 100);
    $scope.pixelsHeight = $scope.ReaderimgHeight - $scope.ReaderimgHeight * ($scope.getPerDecresceVal / 100);
    var convertdCanvasWidth = $scope.pixelsWid;
    var convertdCanvasHeight = $scope.pixelsHeight;
    var image = new Image();
    image.src = $scope.targetImage;
    image.onload = function() {
      image.src = $scope.targetImage;
      width = image.width;
      height = image.height;
      var convertCanvas = document.getElementById("modalCanvas");
      var ctx = convertCanvas.getContext("2d");


      if ($(window).width() <= 768) {
        var getModalWidth = $(window).width();
        var popupWidth = getModalWidth - 15;
      } else {
        var getModalWidth = $("#profileCropModal .cropModalContent").width();
        var popupWidth = getModalWidth - 32;
      }
      if (width > height) {
        if (width > popupWidth) {
          height *= popupWidth / width;
          width = popupWidth;
        }
      } else {
        if (height > popupWidth) {
          width *= popupWidth / height;
          height = popupWidth;
        }
      }
      convertCanvas.width = width;
      convertCanvas.height = height;
      $scope.convertdCanvasWidth = width;
      $scope.convertdCanvasHeight = height;
      if (window.device) {
        $('.cropModalContent').width(width);
      }
      ctx.drawImage(image, 0, 0, $scope.convertdCanvasWidth, $scope.convertdCanvasHeight);
      fitToContainer();
      image.src = $scope.targetImage;
      $scope.ModalCropImage = convertCanvas.toDataURL();
      $scope.$apply();
    }

    function fitToContainer() {
      $scope.bindDrag();
    }
  }


  $('#profileCropModal').modal({
    backdrop: 'static',
    keyboard: false,
    show: false
  });
  //Resize and Crop End

  $scope.uploadProfileCover = function() {
    if (window.device) {
      $scope.openCamera();
    } else {
      //console.log('filechooser');
      try {
        $("#uploadProfileCvr").change(function() {
          if (this.files && this.files[0]) {
            if (this.files[0].name.split('.').pop().toLowerCase() == 'png' || this.files[0].name.split('.').pop().toLowerCase() == 'jpg' || this.files[0].name.split('.').pop().toLowerCase() == 'jpeg') {
              var FR = new FileReader();
              FR.onload = function(e) {

                $scope.newProfileBackground = e.target.result;
                //$('#profileImg').attr("src", $scope.newProfileImage);

                $scope.currentUser.profileBackground = e.target.result;
                $scope.$apply();
              };

              if (this.files[0].size > 5000000) {
                if (window.device) {
                  navigator.notification.alert("Image size is too large.  Please upload below 5MB");
                } else {
                  alert("Image size is too large.  Please upload above 500KB");
                }
                return;
              }
              if (this.files[0].size < 100000) {

                if (window.device) {
                  navigator.notification.alert("Image size is too small. Please upload above 100KB");
                } else {
                  alert("Image size is too small. Please upload above 100KB");
                }
                return;
              }
              FR.readAsDataURL(this.files[0]);
              $("#uploadProfileCvr")[0].value = '';
            } else {
              if (window.device) {
                navigator.notification.alert("Only png, jpg file types are allowed.", null, '', 'Ok');
              } else {
                alert("Only png, jpg file types are allowed.");
              }
            }
          }
        });
        document.getElementById("uploadProfileCvr").click();
      } catch (e) {
        alert(e);
      }
    }
  };

  function updateUserProfile(callBack) {
    url = '/unifydidentity/user';
    $rootScope.callAPI(url, 'POST', $scope.currentUser, function(res) {
      $.unblockUI();
      if (!res.data) {
        if (window.device) {
          navigator.notification.alert(
            'Unable to register user.', // message
            null, // callback
            '', // title
            'OK' // buttonName
          );
        } else {
          alert('Unable to register user.');
