function pageprocessorWebpageone(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {

  var iabRef;
  if (window.device) {
    $.blockUI();
    iabRef = window.open(pagedef.url, "_blank", "location=no,hidden=yes,enableViewportScale=yes,clearcache=yes");
  } else {
    iabRef = window.open(pagedef.url, "_blank");

  }

  if (!window.device) {
    window.history.back();
  }

  var hideBlockUi = function() {
    $.unblockUI();
    iabRef.show();
  };
