    $.ajax({
      url: "https://" + $rootScope.user.domain + "/getLinkPreview",
      method: "POST",
      data: data,
      success: function(data) {
        console.log("data", data);
        $scope.previewData = [...data, ...$scope.previewData];
        if (data.length > 0) {
          $scope.urlsForPreview(data);
        }
      },
      error: function(e) {
        $.unblockUI();
        alert("Error : " + e);
      }
    });
  }



  Array.prototype.remove = function() {
    var what, a = arguments,
      L = a.length,
      ax;
    while (L && this.length) {
      what = a[--L];
      while ((ax = this.indexOf(what)) != -1) {
        this.splice(ax, 1);
      }
    }
    return this;
  }

  /*   $scope.loadUserPref = function() {
