          }
        }
      return flag;
    };

    $scope.validateAllGroup = function() {

      var flag = true;
      var field = ($scope.external) ? "roleName" : "group";
      if ($rootScope.groupsMessaging && $rootScope.selectedGroupMessaging) {
        for (var i = 0; i < $rootScope.groupsMessaging.length; i++) {
          if ($rootScope.selectedGroupMessaging.indexOf($rootScope.groupsMessaging[i][field]) === -1) {
            flag = false;
            break;
          }
        }
      }
      return flag;


    }

    $scope.groupSelected = function(g, e) {
      e.stopPropagation();
      e.preventDefault();
      console.log(g);
      var field = ($scope.external) ? "roleName" : "group";
      if (g === 'All') {
        console.log("All selected");
        if (!$scope.validateAllGroup()) {
          $rootScope.selectedGroupMessaging = [];
          for (var i = 0; i < $rootScope.groupsMessaging.length; i++) {
            $rootScope.selectedGroupMessaging.push($rootScope.groupsMessaging[i][field]);
          }
        } else {
          $rootScope.selectedGroupMessaging = [];
        }
      } else {
        var index = $rootScope.selectedGroupMessaging.indexOf(g[field]);
        if (index === -1) {
          $rootScope.selectedGroupMessaging.push(g[field]);
        } else {
          $rootScope.selectedGroupMessaging.splice(index, 1);
        }
      }
    };

    $scope.genderSelected = function(g, e) {
      e.stopPropagation();
      e.preventDefault();
      $rootScope.selectedUsersMessaging = [];
      if (g === 'All') {
        if (!$scope.validateAllGender()) {
          $rootScope.selectedGenderMessaging = [];
          for (var i = 0; i < $rootScope.gendersMessaging.length; i++) {
            $rootScope.selectedGenderMessaging.push($rootScope.gendersMessaging[i]['gender']);
          }
        } else {
          $rootScope.selectedGenderMessaging = [];
        }
      } else {
        var index = $rootScope.selectedGenderMessaging.indexOf(g['gender']);

        if (index === -1) {
          $rootScope.selectedGenderMessaging.push(g['gender']);
        } else {
          $rootScope.selectedGenderMessaging.splice(index, 1);
        }
      }

    };

    $scope.validateConnectTags = function(c) {
      return !c.enable || $scope.connectTags.indexOf('all') !== -1
    };
    $scope.categorySelected = function(c, e) {
      e.stopPropagation();
      e.preventDefault();

      if (c === 'all') {

        var index = $rootScope.connectTags.indexOf(c);
        $rootScope.connectTags = [];
        $scope.connectTagsName = [];

        if (index === -1) {
          $rootScope.connectTags.push('all');
          $scope.connectTagsName.push('All');
        } else {
          $rootScope.connectTags.splice(index, 1);
          $scope.connectTagsName.splice(index, 1);
        }
      } else {
        if ($scope.validateConnectTags(c)) {
          return;
        }

        var allIndex = $rootScope.connectTags.indexOf('all');
        if (allIndex !== -1) {
          $rootScope.connectTags.splice(allIndex);
          $scope.connectTagsName.splice(allIndex);

        }

        var index = $rootScope.connectTags.indexOf(c['id']);
        if (index === -1) {
          /*  if ($rootScope.connectTags.length === 3) {
              $scope.showAlert('You can select up to 3 tags.');
              return;
            } */
          $rootScope.connectTags.push(c['id']);
          $scope.connectTagsName.push(c['category']);

        } else {
          $rootScope.connectTags.splice(index, 1);
          $scope.connectTagsName.splice(index, 1);
        }
      }

    };


