      ssoEndpoint = ssoEndpoint.substr(0, ssoEndpoint.length - 1);
    }
    console.log("USER ROLES:");
    console.log($rootScope.user.role);

    var qlSecuredListApplUrl = ssoEndpoint + "/admin/open/" + $rootScope.qlTenantId + "/api/list/applications?roles=" + $rootScope.user.role.toString();
    $http({
      method: proxyMethod,
      url: qlSecuredListApplUrl
    }).then(function(response, status, headers, config) {
      $scope.QLSSOApps = [];
      angular.forEach(response.data.applicationDefinitions, function(value, key) {
        if (value.description == "Student Email<br>") {
          value.description = "Student Email"
        }
        $scope.QLSSOApps.push(value)
      });
      $rootScope.isblocking = false;
      $.unblockUI();
    }, function(data, status, headers, config) {
      $.unblockUI();
      console.log("*** Error in opening QL SSO Applets:");
      console.log(data);
      $scope.QLSSOApps = [];
    });

  }

  initApp();


  $scope.openSSOApplet = function(app) {
