                  'Couldnt find Group ' + $scope.email + ".", // message
                  null, // callback
                  '', // title
                  'OK' // buttonName
                );
              } else {
                alert('Couldnt find Group ' + $scope.email + ".");
              }
              return;
            }
          });
        } else {
          $scope.category.whatsupAdmins.push($scope.email);
          $scope.update(function(res) {
            $scope.email = '';
            $scope.isValid = false;
          });
        }
      };

      $scope.registerAdmin = function(data) {
        var url = '/unifydidentity/user';

        console.log(url);
        $rootScope.callAPI(url, 'POST', data, function(res) {

          var data = res.data;


          $rootScope.firstTimeUser = false;

          $rootScope.user['id'] = data['id'];
          $rootScope.user['profileBackground'] = data['profileBackground'];
          $rootScope.user['firstName'] = data['firstName'];
          $rootScope.user['lastName'] = data['lastName'];
          $rootScope.user['profileImage'] = data['profileImage'];
          $rootScope.user['role'] = data['role'];
          $rootScope.user['tenant'] = data['tenant'];
          $rootScope.user['email'] = data['email'];
          $rootScope.user['phone'] = data['phone'];
          $rootScope.user['areaOfInterest'] = data['areaOfInterest'];
          $rootScope.user['channels'] = data['channels'];
        });
      }


      $scope.registerUser = function() {

        console.log($rootScope.user['areaOfInterest']);

        var pref = [];
        for (var i = 0; i < $rootScope.category.length; i++) {
          if (!$rootScope.category[i].rename)
            pref.push($rootScope.category[i].id)
        }

        if (pref.length === 0)
          return;

        //alert(pref);

        var data = {
          firstName: $rootScope.user['firstName'],
          lastName: $rootScope.user['lastName'],
          profileBackground: $rootScope.user['profileBackground'],
          profileImage: $rootScope.user['profileImage'],
          role: $rootScope.user['role'],
          tenant: $rootScope.user['tenant'],
          email: $rootScope.user['email'],
          countryCode: $rootScope.user['countryCode'],
          phone: $rootScope.user['phone'],
          provider: $rootScope.user['provider'],
          gender: $rootScope.user['gender'],
          channels: ['email', 'push']
        };

        if ($rootScope.user['id'])
          data['id'] = $rootScope.user['id'];


        if (!$rootScope.user['areaOfInterest']) {
          //alert('first')
          data['areaOfInterest'] = pref
        } else {

          var update = false;
          console.log('existing')
          for (var i = 0; i < pref.length; i++) {
            if ($rootScope.user['areaOfInterest'].indexOf(pref[i]) === -1) {
              console.log(pref[i] + ' not added in pref');
              update = true;
              break;
            }
          }

          console.log(update);

          if (update)
            data['areaOfInterest'] = pref;
          else
            return;
        }

        $.blockUI();
        $http({
          "method": 'POST',
          "url": 'https://pwms.quicklaunchsso.com/api/open/validateUserName?tenantDomain=' + $rootScope.user.tenantdomain + '&tenantId=' + $rootScope.user.qlId + '&userName=' + $rootScope.username,
          data: {}
        }).then(function successCallback(res) {
            $.unblockUI();
            if (!res.data.errorMessage) {
              if (!res.data.userVerification.phoneNumber) {
                $http({
                  "method": 'POST',
                  "url": 'https://pwms.quicklaunchsso.com/api/open/getConfiguredEmailId',
                  data: res.data.userVerification
                }).then(function successCallback(res) {
                  try {
                    data.phone = res.data.primaryPhone;
                    data.provider = res.data.userVerification.primaryPhoneCarrier;
                    console.log('UserDetails', data);
                    $scope.registerdevicePush(data);
                  } catch (e) {
                    console.log(e);
                    $scope.registerdevicePush(data);
                  }
                }, function errorCallback(err) {
                  $.unblockUI();
                  $scope.registerdevicePush(data);
                });
              } else {
                data.phone = res.data.userVerification.phoneNumber;
                data.provider = res.data.userVerification.phoneCarrier;
                console.log('UserDetails', data);
                $scope.registerdevicePush(data);
              }
            }
          },
          function errorCallback(err) {
            $.unblockUI();
            $scope.registerAdmin(data);
          });
      }

      $scope.registerdevicePush = function(data) {
        /*  Getting device push IDs when running on mobile device */
        try {
          if (window.device) {
            var push = PushNotification.init({
              android: {
                senderID: "1012473507211"
              },
              browser: {
                pushServiceURL: 'http://push.api.phonegap.com/v1/push'
              },
              ios: {
                alert: "true",
                badge: "true",
                sound: "true"
              },
              windows: {}
            });

            PushNotification.hasPermission(function(permission) {
              if (permission.isEnabled) {
                push.on('registration', function(res) {
                  data.devices = [{
                    "id": res.registrationId,
                    "type": device.platform
                  }];
                  $scope.registerAdmin(data);
                });
              } else {
                $scope.registerAdmin(data);
              }
            });

            $rootScope.inAppNotificationHandler(push);
          } else {
            $scope.registerAdmin(data);
          }
        } catch (e) {
          alert(e)
        }
      }
      var specialChars = "<>!#$%^&*()+[]{}?:;|'\"\\,/~`=";
      $scope.checkForSpecialChar = function(string) {
        for (i = 0; i < specialChars.length; i++) {
          if (string.indexOf(specialChars[i]) > -1) {
            return true
          }
        }
        return false;
      }

      $scope.showUserGroupDropdown = function() {
        console.log($scope.email);
        $scope.searchCollection = [];
        $scope.errorMessage = "";
        $scope.showErrorMessage = false;
        if (!$scope.email) {
          return;
        }
        if (!$scope.email || $scope.email.length < 3) {
          $('#noDataFoundFor').show();
          $scope.showErrorMessage = true;
          $scope.errorMessage = "Please enter at least 3 characters";
          return;
        }
        if ($scope.checkForSpecialChar($scope.email)) {
          $scope.showErrorMessage = true;
          $scope.errorMessage = "No data found";
          return;
        }
        $scope.searchCollection = [];
        var userUrl = "/unifydidentity/user/search/findByName?name=" + $scope.email;
        /*var userUrl = "/unifydidentity/user/search/findByEmailStartingWith?email=" + $scope.email; */

        $scope.callAPI(userUrl, 'GET', '', function(res) {
          var groupUrl = "/unifydplatform/group/search/findAllByGroupStartingWithIgnoreCase?group=" + $scope.email;
          $scope.callAPI(groupUrl, 'GET', '', function(resp) {
            if (res && res.data && res.data.content && res.data.content.length > 0) {
              angular.forEach(res.data.content, function(value, key) {
                $scope.searchCollection.push(value.email);
              });
            } else if (res && res.data && res.data.content && res.data.content.length == 0) {
              $scope.showErrorMessage = true;
              $scope.errorMessage = "No data found";
            }
            if (resp && resp.data && resp.data._embedded && resp.data._embedded.group && resp.data._embedded.group.length > 0) {
              angular.forEach(resp.data._embedded.group, function(value, key) {
                $scope.searchCollection.push(value.group);
              });
            }
          });

        });



      }

      $scope.groupSelected = function(g, e) {
        e.stopPropagation();
        e.preventDefault();
        console.log(g);
        if ($scope.category.whatsupAdmins.indexOf(g) == -1) {
          if ($scope.maxWhatsupAdmins && $scope.maxWhatsupAdmins > 0 && $scope.maxWhatsupAdmins <= $scope.category.whatsupAdmins.length) {
            return;
          }
          $scope.category.whatsupAdmins.push(g);
        } else {
          $scope.category.whatsupAdmins.splice($scope.category.whatsupAdmins.indexOf(g), 1);
        }
        $scope.update(function(res) {
          console.log(res);
        });
      };

      $scope.notificationSettingChange = function(notificationSetting, e) {

        e.stopPropagation();
        e.preventDefault();

        $scope.category.notificationSetting[notificationSetting] = !$scope.category.notificationSetting[notificationSetting];

        var keys = Object.keys($scope.category.notificationSetting);

        if (notificationSetting === 'override') {

          for (var i = 0; i < keys.length; i++) {
            if (keys[i] === 'override')
              continue;

            if (!$scope.category.notificationSetting[keys[i]]) {
              if (window.device) {
                navigator.notification.alert(
                  'You can override user setting only when all are enabled.', // message
                  null, // callback
                  '', // title
                  'OK' // buttonName
                );
              } else {
                alert('You can override user setting only when all are enabled.');
              }

              $scope.category.notificationSetting.override = !$scope.category.notificationSetting.override;
              $scope.$apply();
              return;

            }

          }




        } else {

          for (var i = 0; i < keys.length; i++) {
            if (keys[i] === 'override')
              continue;

            if (!$scope.category.notificationSetting[keys[i]]) {
              $scope.category.notificationSetting.override = false;
              break;
            }

          }



        }

        var categoryUrl = url + '/' + $scope.category['id'];

        $scope.callAPI(categoryUrl, 'PUT', $scope.category, function(res) {



        });
